#include <curl/curl.h>
#include <stdexcept>

using namespace std;

string es_connect;

void read_connect_conf_file(){
	ifstream myfile ("/opt/telepath/db/elasticsearch/config/connect.conf");
	if (myfile.is_open()){
		getline (myfile,es_connect);
		myfile.close();
	}else{
		es_connect = "http://localhost:9200";
	}
}

void dropprinting()
{
	return;
}

struct MemoryStruct{
	char *memory;
	size_t size;
};

static size_t WriteMemoryCallback(void *contents, size_t size, size_t nmemb, void *userp)
{
	size_t realsize = size * nmemb;
	struct MemoryStruct *mem = (struct MemoryStruct *)userp;

	mem->memory = (char*)realloc(mem->memory, mem->size + realsize + 1);
	if(mem->memory == NULL) {
		/* out of memory! */ 
		printf("not enough memory (realloc returned NULL)\n");
		return 0;
	}

	memcpy(&(mem->memory[mem->size]), contents, realsize);
	mem->size += realsize;
	mem->memory[mem->size] = 0;

	return realsize;
}

void es_get_config(string url,string & output){
	CURL *curl;
	curl = curl_easy_init();
	struct MemoryStruct chunk;
	string tmp;

	chunk.memory = (char*)malloc(1);  /* will be grown as needed by the realloc above */ 
	chunk.size = 0;

	url = es_connect + url;
	curl_easy_setopt(curl,CURLOPT_CUSTOMREQUEST,"GET"); 
	curl_easy_setopt(curl, CURLOPT_URL,url.c_str());
	curl_easy_setopt(curl, CURLOPT_WRITEFUNCTION, WriteMemoryCallback);
	curl_easy_setopt(curl, CURLOPT_WRITEDATA, (void *)&chunk );
	curl_easy_perform(curl);

	tmp = chunk.memory+10;
	try{
		output.assign(tmp.begin(),tmp.end()-2);
	}
	catch (const std::length_error& le) {
		syslog(LOG_NOTICE,"Cannot Connent to Elasticsearch[!!!] - Please Check That The Address \"%s\" Is Correct.",es_connect.c_str());
		output = "0";
	}

	free(chunk.memory);
	curl_easy_cleanup(curl);
}


void es_mapping(string url,string postparams){
	CURL *curl;
	curl = curl_easy_init();

	url = es_connect + url;
	curl_easy_setopt(curl,CURLOPT_CUSTOMREQUEST,"POST"); 
	curl_easy_setopt(curl, CURLOPT_URL,url.c_str());
	curl_easy_setopt(curl, CURLOPT_POSTFIELDS,postparams.c_str());
	curl_easy_setopt(curl, CURLOPT_WRITEFUNCTION, dropprinting);
	curl_easy_perform(curl);

	curl_easy_cleanup(curl);
}

void es_insert(string url,string postparams){
	CURL *curl;
	curl = curl_easy_init();

	url = es_connect + url;
	curl_easy_setopt(curl,CURLOPT_CUSTOMREQUEST,"PUT"); 
	curl_easy_setopt(curl, CURLOPT_URL,url.c_str());
	curl_easy_setopt(curl, CURLOPT_POSTFIELDS,postparams.c_str());
	curl_easy_setopt(curl, CURLOPT_WRITEFUNCTION, dropprinting);
	curl_easy_perform(curl);

	curl_easy_cleanup(curl);
}

